@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 222 47% 6%;
    --foreground: 210 40% 98%;

    --card: 222 47% 9%;
    --card-foreground: 210 40% 98%;

    --popover: 222 47% 9%;
    --popover-foreground: 210 40% 98%;

    --primary: 262 83% 58%;
    --primary-foreground: 210 40% 98%;

    --secondary: 217 91% 60%;
    --secondary-foreground: 210 40% 98%;

    --muted: 217 33% 17%;
    --muted-foreground: 215 20% 65%;

    --accent: 262 83% 58%;
    --accent-foreground: 210 40% 98%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 210 40% 98%;

    --border: 217 33% 17%;
    --input: 217 33% 17%;
    --ring: 262 83% 58%;

    --radius: 0.75rem;

    /* Custom design tokens */
    --gradient-primary: linear-gradient(135deg, hsl(262 83% 58%) 0%, hsl(217 91% 60%) 100%);
    --gradient-glow: linear-gradient(135deg, hsl(262 83% 70% / 0.4) 0%, hsl(217 91% 70% / 0.4) 100%);
    --gradient-card: linear-gradient(180deg, hsl(222 47% 11%) 0%, hsl(222 47% 8%) 100%);
    --gradient-hero: radial-gradient(ellipse 80% 50% at 50% -20%, hsl(262 83% 58% / 0.3), transparent);

    --shadow-glow: 0 0 30px hsl(262 83% 58% / 0.3);
    --shadow-glow-lg: 0 0 60px hsl(262 83% 58% / 0.4);
    --shadow-glass: 0 8px 32px hsl(0 0% 0% / 0.3);

    --glass-bg: hsl(222 47% 11% / 0.7);
    --glass-border: hsl(217 33% 25% / 0.5);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground antialiased;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer components {
  .glass-card {
    background: var(--glass-bg);
    backdrop-filter: blur(20px);
    border: 1px solid var(--glass-border);
  }

  .gradient-text {
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .gradient-border {
    position: relative;
    background: var(--gradient-card);
    border-radius: var(--radius);
  }

  .gradient-border::before {
    content: '';
    position: absolute;
    inset: 0;
    padding: 1px;
    border-radius: inherit;
    background: var(--gradient-primary);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    pointer-events: none;
  }

  .glow-effect {
    box-shadow: var(--shadow-glow);
    transition: box-shadow 0.3s ease;
  }

  .glow-effect:hover {
    box-shadow: var(--shadow-glow-lg);
  }

  .shimmer {
    background: linear-gradient(
      90deg,
      transparent 0%,
      hsl(262 83% 70% / 0.1) 50%,
      transparent 100%
    );
    background-size: 200% 100%;
    animation: shimmer 2s linear infinite;
  }

  @keyframes shimmer {
    0% { background-position: -200% 0; }
    100% { background-position: 200% 0; }
  }

  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .upload-zone {
    @apply relative flex flex-col items-center justify-center rounded-xl border-2 border-dashed border-border/50 bg-card/50 transition-all duration-300;
  }

  .upload-zone:hover {
    @apply border-primary/50 bg-card/80;
    box-shadow: var(--shadow-glow);
  }
}

